import React from "react";
import Button from 'react-bootstrap/Button';
import Modal from 'react-bootstrap/Modal';
import Form from 'react-bootstrap/Form';
import axios from "axios"

class FormModal extends React.Component{

    showModal = React.useRef(props.showModal);

    constructor(props)
    {
        super(props);
        this.state = {
            email: "",
            profession: "",
            suggestion: "",
            refresh: false
        }

        
    }

    handleChange = (e) =>{
        //e && e.preventDefault();
        //const {email, profession, suggestion} = e.target;
        // setFormData({
        //   ...formData,
        //   [e.target.name]: e.target.value
        // });
      }
    
    handleClose = (e) => {
        e && e.preventDefault();
        showModal.current = false;
        setRefresh(!refresh);
        };
    
    handleShow = (e) => {
        e.preventDefault();
        showModal.current = true;
        setRefresh(!refresh);
        };

    handleSubmit = (e) =>{
        e.preventDefault();
        alert(JSON.stringify(formData));
        axios({
        
            // Endpoint to send files
            url: "https://iz4tu2qmv5.execute-api.us-east-2.amazonaws.com/Test/users",
            method: "POST",
            //headers: {

                // Add any auth token here
            //  authorization: "your token comes here",
            //},
            data: formData })
            .then((res) => { 
                if (res.Message === "Success")
                {
                    //TODO:
                    alert("Success");
                }
            })
            .catch((err) => { 
            //TODO:
            alert(err && err.Body);
            });
        showModal.current = false;
        setRefresh(!refresh);
    }

    render(){
        return (
            <>
                <Modal
                show={showModal.current}
                onHide={handleClose}
                backdrop="static"
                keyboard={false}
                centered>
        
                <Modal.Header closeButton>
                <Modal.Title>Share your suggestions or comments</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                <Form>
                    <Form.Group className="mb-3" controlId="formData.email">
                    <Form.Label>Email address</Form.Label>
                    <Form.Control
                        id= "email"
                        type="email"
                        placeholder="name@example.com"
                        value={formData.email}
                        onChange={handleChange}
                        autoFocus
                    />
                    </Form.Group>
                    <Form.Group className="mb-3" controlId="formData.profession">
                    <Form.Label>Profession</Form.Label>
                    <Form.Control
                        id= "profession"
                        type="text"
                        value={formData.profession}
                        onChange={handleChange}
                    />
                    </Form.Group>
                    <Form.Group
                    className="mb-3"
                    controlId="formData.suggestion">
                    <Form.Label>Suggestions/comments</Form.Label>
                    <Form.Control id="suggestion" as="textarea" rows={3} cols={10}
                    value={formData.suggestion}
                    onChange={handleChange}/>
                    </Form.Group>
                </Form>
                </Modal.Body>
                <Modal.Footer>
                <Button variant="secondary" onClick={handleClose}>
                    Close
                </Button>
                <Button variant="primary" onClick={handleSubmit}>Submit</Button>
                </Modal.Footer>
            </Modal>
            </>
        );
    }
        
}

export default FormModal;
