import React from "react";
import FormModal from "./FormModal";

import Container from 'react-bootstrap/Container';
import Nav from 'react-bootstrap/Nav';
import Navbar from 'react-bootstrap/Navbar';
import NavDropdown from 'react-bootstrap/NavDropdown';

import "bootstrap/dist/css/bootstrap.min.css";

export default function ZNavbar()
{

  //alert('Nav-No: ' + Math.random(1000));
  
  const showModal = React.useRef(false);
  const formModalKey = React.useRef(1);
  const [refresh, setRefresh] = React.useState(false);

  const handleShow= (e) =>{
    e && e.preventDefault();
    
    showModal.current = true;
    //alert("ShowModel::" + showModal.current);
    setRefresh(!refresh);
    
  }

  formModalKey.current = formModalKey.current + 1;

  return (
  <>
      <Navbar className="bg-body-tertiary">
        <Container>
          <Navbar.Brand href="#home">ZEBRA.AI</Navbar.Brand>
          <Navbar.Toggle />
          <Navbar.Collapse className="justify-content-end">
            <Navbar.Text>
              <a href="" onClick={handleShow}>Suggestions</a>
            </Navbar.Text>
          </Navbar.Collapse>
        </Container>
    </Navbar>

    {showModal.current && <FormModal showModal={true} key={formModalKey.current}></FormModal>}
  </>
  );

}
