import React from "react";
import FormModal from "./FormModal";

export default function Navbar()
{

  //alert('Nav-No: ' + Math.random(1000));
  
  //const showModal = React.useRef(false);
  const [showModal, setShowModal] = React.useState(false);

  const handleShow= (e) =>{
    e && e.preventDefault();
    // showModal.current = true;
    // setRefresh(!refresh);
    setShowModal(true);
  }

  alert("ShowModel::" + showModal);
  return (
  <>
    <nav>
    <h1>Fundu.AI</h1> 
    
    <list>
        <ul><a href="">Menu</a></ul>
        <ul><a href="">Curate</a></ul>
        <ul><a href="" onClick={handleShow}>Signup</a></ul>
    </list>
    </nav>

    {showModal && <FormModal></FormModal>}
  </>
  );

}
