import React from "react";
import FormModal from "./FormModal";

export default function Navbar()
{

  //alert('Nav-No: ' + Math.random(1000));
  
  const showModal = React.useRef(false);
  const formModalKey = React.useRef(1);
  const [refresh, setRefresh] = React.useState(false);

  const handleShow= (e) =>{
    e && e.preventDefault();
    
    showModal.current = true;
    alert("ShowModel::" + showModal.current);
    setRefresh(!refresh);
    
  }

  formModalKey.current = formModalKey.current + 1;
  return (
  <>
    <nav>
    <h1>Fundu.AI</h1> 
    
    <list>
        <ul><a href="">Menu</a></ul>
        <ul><a href="">Curate</a></ul>
        <ul><a href="" onClick={handleShow}>Signup</a></ul>
    </list>
    </nav>

    {showModal.current && <FormModal showModal={true} key={formModalKey.current}></FormModal>}
  </>
  );

}
