import React from "react";
import Button from 'react-bootstrap/Button';
import Modal from 'react-bootstrap/Modal';
import Form from 'react-bootstrap/Form';
import axios from "axios"

import "bootstrap/dist/css/bootstrap.min.css";

export default function Navbar()
{
  
  alert('Nav-No: ' + Math.random(1000));

  const [refresh, setRefresh]  = React.useState(false);
  const showModal = React.useRef(false);

  const handleClose = (e) => {
    e && e.preventDefault();
    showModal.current = false;
    setRefresh(!refresh);
  };
  const handleShow = (e) => {
    e.preventDefault();
    showModal.current = true;
    setRefresh(!refresh);
  };

  const handleSubmit = (e) =>{
    e.preventDefault();
    axios({
 
      // Endpoint to send files
      url: "https://iz4tu2qmv5.execute-api.us-east-2.amazonaws.com/Test/users",
      method: "POST",
      //headers: {

          // Add any auth token here
        //  authorization: "your token comes here",
      //},
      data: formData })
      .then((res) => { 
          if (res.Message === "Success")
          {
              //TODO:
          }

      })
      .catch((err) => { });
    showModal.current = false;
    setRefresh(!refresh);
  }

    return (
    <>
      <nav>
      <h1>Fundu.AI</h1> 
      
      <list>
          <ul><a href="">Menu</a></ul>
          <ul><a href="">Curate</a></ul>
          <ul><a href="" onClick={handleShow}>Signup</a></ul>
      </list>
      </nav>


      <Modal
      show={showModal.current}
      onHide={handleClose}
      backdrop="static"
      keyboard={false}
      centered>

      <Modal.Header closeButton>
        <Modal.Title>Share your suggestions or comments</Modal.Title>
      </Modal.Header>
      <Modal.Body>
        <Form>
          <Form.Group className="mb-3" controlId="formData.email">
            <Form.Label>Email address</Form.Label>
            <Form.Control
              type="email"
              placeholder="name@example.com"
              autoFocus
            />
          </Form.Group>
          <Form.Group
            className="mb-3"
            controlId="formData.suggestion">
            <Form.Label>Suggestions/comments</Form.Label>
            <Form.Control as="textarea" rows={3} cols={10}/>
          </Form.Group>
        </Form>
      </Modal.Body>
      <Modal.Footer>
        <Button variant="secondary" onClick={handleClose}>
          Close
        </Button>
        <Button variant="primary" onClick={handleSubmit}>Submit</Button>
      </Modal.Footer>
      </Modal>
    </>
    );

}
