import React from "react";
import Button from 'react-bootstrap/Button';
import Modal from 'react-bootstrap/Modal';
import Form from 'react-bootstrap/Form';
import axios from "axios"

class FormModal extends React.Component{

    constructor(props)
    {
        super(props);
        this.state = {
            email: "",
            profession: "",
            suggestion: "",
            refresh: false,
            showModal: props.showModal
        }
    }

    // setFormData = ({email, profession, suggestion}) =>{
    //     this.state.email = email? this.state.email: email;
    //     this.state.profression = profession? this.state.profession: profession;
    //     this.state.suggestion = suggestion? this.state.suggestion: suggestion;
    // }

    // handleChange = (e) =>{
    //     //e && e.preventDefault();
    //     const {email, profession, suggestion} = e.target;
    //     setFormData({email, profession, suggestion});
    //   }
    
    handleClose = (e) => {
        e && e.preventDefault();
        this.state.showModal = false;
        this.status.refresh = !this.status.refresh;
        };
    
    handleShow = (e) => {
        e && e.preventDefault();
        this.state.showModal = false;
        this.status.refresh = !this.status.refresh;
        };

    // handleSubmit = (e) =>{
    //     e.preventDefault();
    //     alert(JSON.stringify(formData));
    //     axios({
        
    //         // Endpoint to send files
    //         url: "https://iz4tu2qmv5.execute-api.us-east-2.amazonaws.com/Test/users",
    //         method: "POST",
    //         //headers: {

    //             // Add any auth token here
    //         //  authorization: "your token comes here",
    //         //},
    //         data: {
    //             email: this.state.email,
    //             profession: this.state.profession,
    //             suggestion: this.state.suggestion
    //         } 
    //         })
    //         .then((res) => { 
    //             if (res.Message === "Success")
    //             {
    //                 //TODO:
    //                 alert("Success");
    //             }
    //         })
    //         .catch((err) => { 
    //         //TODO:
    //         alert(err && err.Body);
    //         });
    //     this.state.showModal = false;
    //     this.status.refresh = !this.status.refresh;
    // }

    render(){
        return (
            <>
                <Modal
                show={this.state.showModal}
                onHide={handleClose}
                backdrop="static"
                keyboard={false}
                centered>
        
                <Modal.Header closeButton>
                <Modal.Title>Share your suggestions or comments</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                <Form>
                    <Form.Group className="mb-3" controlId="formData.email">
                    <Form.Label>Email address</Form.Label>
                    <Form.Control
                        id= "email"
                        type="email"
                        placeholder="name@example.com"
                        value={this.state.email}
                        onChange={handleChange}
                        autoFocus
                    />
                    </Form.Group>
                    <Form.Group className="mb-3" controlId="formData.profession">
                    <Form.Label>Profession</Form.Label>
                    <Form.Control
                        id= "profession"
                        type="text"
                        value={this.state.profession}
                        onChange={handleChange}
                    />
                    </Form.Group>
                    <Form.Group
                    className="mb-3"
                    controlId="formData.suggestion">
                    <Form.Label>Suggestions/comments</Form.Label>
                    <Form.Control id="suggestion" as="textarea" rows={3} cols={10}
                    value={this.state.suggestion}
                    onChange={handleChange}/>
                    </Form.Group>
                </Form>
                </Modal.Body>
                <Modal.Footer>
                <Button variant="secondary" onClick={handleClose}>
                    Close
                </Button>
                <Button variant="primary" onClick={handleSubmit}>Submit</Button>
                </Modal.Footer>
            </Modal>
            </>
        );
    }
        
}

export default FormModal;
